import React from 'react';
import vivek from '../vivek.jpg';

const AboutUs = () => {
    return (
        <div className="container3" style={{ textAlign: 'left' }}>
            <div className="profile">
                <p>
                    Welcome to SendText, where we transform how you communicate. Our goal is to offer a secure and anonymous platform for sending text messages without requiring email addresses or phone numbers.
                    We focus on your privacy and aim to provide a seamless messaging experience. With just a six-digit code, you can send messages without the concern of spam or unwanted interruptions.
                    Experience safe and private communication like never before with us!
                </p>
                <div>
                <img style={{ float: 'left' }} src={vivek} alt="Vivek Paghadar" className="profile-photo" />

                    <h2>Vivek Paghadar</h2>
                    <p>
                        Hi! I'm the person behind SendTextAnywhere. I am constantly working
                        to enhance this platform and improve your experience. Thank you for being part of our journey!
                    </p>
                </div>
            </div>
        </div>
    );
};

export default AboutUs;
