import React, { useState } from "react";
import './App.css'; // External CSS for styling (optional)

function ContectUs() {
  const [text, setText] = useState(""); // To manage the input value

  
  const handleChange = (e) => {
    setText(e.target.value);
  };

  // Handle Send button click
  const handleSend = () => {
    if (text.trim() !== "") {
      console.log("Text Sent:", text); // Send or process the text
      alert(`Text sent: ${text}`); // Display alert or handle submission
      setText(""); // Clear the input after sending
    } else {
      alert("Please enter some text before sending.");
    }
  };

  return (
    <div className="edit-text-container">
      <label htmlFor="editText">Enter text:</label>
      <input
        id="editText"
        type="text"
        value={text}
        onChange={handleChange}
        spellCheck={false} // Disable spellcheck
        placeholder="Write here..."
        className="edit-text-input"
      />
      <button className="send-button" onClick={handleSend}>
        Send
      </button>
    </div>
  );
}

export default ContectUs;
