import React, { useState } from "react";

const ContectUs = () => {

  const [text, setText] = useState('');

  const handleChange = (event) => {
    setText(event.target.value);
  };

  const handleSend = (event) => {
    event.preventDefault();

    if (text.trim() === '') {
      alert('Please enter feedback');
      return;
    } else {
      window.location.href = "sendanywhere@gmail.com";
      e.preventDefault();
    }
  };

  return (
    <div>
      <div className='row d-flex justify-content-center align-items-center'>
        <div className='col-4 mb-4'>
          <div className='Textarea-Edit'>
            <form id='Form-addText'>
              <div className='mb-3'>
                {/* <label htmlFor="text" className="form-label">Note Text</label> */}
                <textarea spellCheck={false} className="form-control" id="text" name='note' rows="8" placeholder='Write Feedback' onChange={handleChange}
                  value={text} ></textarea>
              </div>
            </form>
          </div>
        </div>
      </div>
      <div className="d-flex justify-content-center">
        <button onClick={handleSend} type="submit" className="btn btn-danger col-2">Send</button>
      </div>
    </div>
  );
};

export default ContectUs;
