import React, { useState } from "react";

const ContectUs = () => {

  const handleSend = async (event) => {
    event.preventDefault();

    let text = event.target.note.value;

    if (text.trim() === '') {
      alert('Text should not be empty');
      return;
    } else {
      alert(`Text sent: ${text}`);
    }

  }

  return (
    <div>
      <div className='row d-flex justify-content-center align-items-center'>
        <div className='col-4 mb-4'>
          <div className='Textarea-Edit'>
            <form id='Form-addText'>
              <div className='mb-3'>
                {/* <label htmlFor="text" className="form-label">Note Text</label> */}
                <textarea spellCheck={false} className="form-control" id="text" name='note' rows="8" placeholder='Write Feedback'></textarea>
              </div>
            </form>
          </div>
        </div>
      </div>
      <div className="d-flex justify-content-center">
        <button onClick={handleSend} type="submit" className="btn btn-danger col-2">Send</button>
      </div>
    </div>
  );
};

export default ContectUs;
