import React, { useState } from "react";

function ContectUs() {
  const [text, setText] = useState(""); 

  
  const handleChange = (e) => {
    setText(e.target.value);
  };

  const FromSend = async (event) => {
    event.preventDefault();
  }

  
  const handleSend = () => {
    if (text.trim() !== "") {
      console.log("Text Sent:", text);
      alert(`Text sent: ${text}`);
      setText("");
    } else {
      alert("Please enter some text before sending.");
    }
  };

  return (
    <div>
      <div className='row d-flex justify-content-center align-items-center'>
            <div className='col-4 mb-4'>
              <div className='Textarea-Edit'>
                <form id='Form-addText' onSubmit={FromSend}>
                  <div className='mb-3'>  
                    {/* <label htmlFor="text" className="form-label">Note Text</label> */}
                    <textarea spellCheck={false} className="form-control" id="text" name='note' rows="8" placeholder='Enter Your Text Here...' onChange={handleChange} value={Text}></textarea>
                  </div>
                </form>
              </div>
            </div>
          </div>
          <div className="d-flex justify-content-center">
    <button type="submit" className="btn btn-danger col-2">Send</button>
  </div>
    </div>
  );
}

export default ContectUs;
