import { useState } from 'react';
import './App.css';
import QRCode from "react-qr-code";
import Headers from './Components/Header';
import AboutUs from './Components/about_us';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

function App() {

  const [Code, setCode] = useState("");
  const [Text, setText] = useState("");
  const [netText, setnetText] = useState("");
  const [ViewType, setViewType] = useState(1);


  const FromSend = async (event) => {
    event.preventDefault();

    let text = event.target.note.value;

    if (text.trim() === '') {
      alert('Text should not be empty');
      return;
    }

    let data = {
      message: text
    };

    // Send the data using fetch
    try {
      const response = await fetch('http://localhost:3001/addtext', {
        method: 'POST', // Set the method to POST
        headers: {
          'Content-Type': 'application/json' // Specify the content type as JSON
        },
        body: JSON.stringify(data) // Convert the data to a JSON string
      });

      const result = await response.json(); // Parse the JSON response from the server
      console.log('Server response:', result);
      setCode(result.code);
      setnetText(result.text);
      setText("");
      setViewType(2) // 2 for barcode
      document.getElementById('Form-addText').reset();
    } catch (error) {
      console.error('Error sending data:', error);
    }

  }

  const Fromget = async (event) => {
    event.preventDefault();

    let code = event.target.receive_code.value;
    let data = {
      code: code
    };

    // Send the data using fetch
    try {
      const response = await fetch('http://localhost:3001/gettext', {
        method: 'POST', // Set the method to POST
        headers: {
          'Content-Type': 'application/json' // Specify the content type as JSON
        },
        body: JSON.stringify(data) // Convert the data to a JSON string
      });

      const result = await response.json(); // Parse the JSON response from the server
      console.log('Server response:', result);
      if (result.success === 402) {
        alert('The code is invalid or has expired');
        return;
      }
      setText(result.text)
      setViewType(3) // 3 for result code
      document.getElementById('Form-getText').reset();
    } catch (error) {
      console.error('Error sending data:', error);
    }

  }

  const noteChange = (e) => {
    setText(e.target.value);
  }

  const handleCopy = (event) => {
    event.preventDefault();
    navigator.clipboard.writeText(Text)
      .then(() => {
        alert('Text copied to clipboard!');
      })
      .catch(err => {
        console.error('Failed to copy: ', err);
      });
  };

  const handleDownload = (event) => {
    event.preventDefault();
    const blob = new Blob([Text], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'sendtextanywhere.txt';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleShare = (event) => {
    event.preventDefault();
    if (navigator.share) {
      navigator.share({
        title: 'Share Text',
        text: Text,
        url: window.location.href, // You can specify a URL if needed
      })
        .then(() => {
          alert('Text shared successfully!');
        })
        .catch(err => {
          console.error('Error sharing: ', err);
        });
    } else {
      alert('Share feature is not supported in this browser.');
    }
  };

  return <Router>
    <Switch>
      <Route path="/" exact>
        <div className="App">
          <Headers theme="light" />
          <div className="container my-4">
            {ViewType === 2 && (
              <div className='row my-5'>
                <div className='col-3'>
                  <div className='Textarea-Edit'>
                    <form id='Form-addText' onSubmit={FromSend}>
                      <div className='text-center mb-3'>
                        <p>Enter the 4-digit code on the receiving device</p>
                        <h1>{Code}</h1>
                        <div className='mt-5'>
                          <QRCode
                            size={256}
                            style={{ height: "auto", maxWidth: "50%", width: "50%" }}
                            value={netText}
                            viewBox={`0 0 256 256`}
                          />
                        </div>
                      </div>
                    </form>
                  </div>
                </div>
                <div className='col-3'></div>
                <div className='col-6'></div>
              </div>
            )}

            {ViewType === 1 && (
              <div className='row'>
                <div className='col-7 '>
                  <div className='Textarea-Edit'>
                    <form id='Form-addText' onSubmit={FromSend}>
                      <div className='mb-3' style={{ height: '47vh' }}>
                        {/* <label htmlFor="text" className="form-label">Note Text</label> */}
                        <textarea className="form-control" id="text" name='note' rows="14" placeholder='Enter Your Text Here...' onChange={noteChange} value={Text}></textarea>
                      </div>
                      <button type="submit" className="btn btn-danger col-2">Send</button>
                    </form>
                  </div>
                </div>
                <div className='col-3'></div>
                <div className='col-2'></div>
              </div>
            )}


            {ViewType === 3 && (
              <div className='row'>
                <div className='col-7'>
                  <div className='Textarea-Edit'>
                    <div className="mb-3">
                      <textarea className="form-control" id="text" name='note' rows="14" placeholder='Enter Your Text Here...' onChange={noteChange} value={Text}></textarea>
                    </div>

                    <div className="d-flex justify-content-end">
                      {/* <button type="button" className="btn btn-danger me-3" onClick={handleCopy}>
                        <i className="fa-solid fa-copy"></i> Copy
                    </button> */}
                      <button type="button" className="btn btn-danger me-3" onClick={handleDownload}>
                        <i className="fa-solid fa-download"></i> Download
                      </button>
                      {/* <button type="button" className="btn btn-danger" onClick={handleShare}>
                        <i className="fa-solid fa-share"></i> Share
                    </button> */}
                    </div>

                  </div>
                </div>
                <div className='col-3'></div>
                <div className='col-2'></div>
              </div>
            )}

            <form className='mt-4' id='Form-getText' onSubmit={Fromget}>
              <div className="mb-3 col-2">
                {/* <label htmlFor="Receive-Code" className="form-label">Receive Code</label> */}
                <input type="text" className="form-control" id="Receive-Code" name='receive_code' placeholder='Receive Code' />
              </div>
              <button type="submit" className="btn btn-danger"><i className="fa-solid fa-download"></i></button>
            </form>
          </div>
        </div>
      </Route>
      <Route path="/about" component={AboutUs} />
    </Switch>
  </Router>;
}

export default App;
