import React, { useState } from "react";
function ContectUs() {
  const [text, setText] = useState(""); // To manage the input value

  const handleChange = (e) => {
    setText(e.target.value); // Update state on input change
  };

  return (
    <div className="edit-text-container">
      <label htmlFor="editText">Enter text:</label>
      <input
        id="editText"
        type="text"
        value={text}
        onChange={handleChange}
        spellCheck={false} // Disable spellcheck (optional)
        placeholder="Write here..."
        className="edit-text-input" // Class for styling
      />
    </div>
  );
}

export default ContectUs;
